import React from "react";
import { Fragment,useState, useEffect } from "react";

import Menu from "../menu/Menu";
import Jumbotron from "./jumbotron/Jumbotron";
import Detalles from "./detalles/Detalles";
import Footer from "../footer/Footer";
import Pais from "../Pais/Pais";

function Local() {

   const [infoArgentina, setInfoArgentina] = useState([]);

      const {
        confirmed,
        recovered,
        deaths,
        country,
        population,
        continent,
        abbreviation,
        location,
        iso,
        capital_city,
        updated,
      } = infoArgentina;
    
    
    useEffect(() => {
        debugger;
    const url = "https://covid-api.mmediagroup.fr/v1/cases?country=Argentina";

    const fetchData = async () => {
      try {
          debugger;
        const response = await fetch(url);
        const json = await response.json();

       

        setInfoArgentina(json.All);
      } catch (error) {
        console.log("error", error);
      }
    };
    fetchData();

    }, []);
  
  return (
    <>
      <Menu />

      <main role="main" className="flex-shrink-0 mt-5">
         <Fragment>
      <main role="main">
        <div className="jumbotron">
          <div className="container">
            <h1 className="display-3">Bienvenido a InfoCovidAR</h1>
            <p>
              En este sitio encontraras informacion relevante respecto al estado
              epidemico de la argentina y el mundo
            </p>
            <p></p>
          </div>
        </div>

        <div className="container">
          <div className="row">
            <Pais key={iso} pais={infoArgentina} />
          </div>
          <hr></hr>
        </div>
      </main>
    </Fragment>
      </main>

      <Footer />
    </>
  );
}

export default Local;
