import React from "react";
import { Fragment, useState, useEffect } from "react";

function Pais({ pais }) {
  const {
    confirmed,
    recovered,
    deaths,
    country,
    population,
    continent,
    abbreviation,
    location,
    iso,
    capital_city,
    updated,
  } = pais;

  return (
    <Fragment>
      <div className="col-md-4">
        <div className="card" style="width: 18rem;">
          <img
            style={{
              resizeMode: "center",
              width: 300,
            }}
            alt="casos"
            src="https://quickchart.io/chart?bkg=white&c={ type: 'bar', data: { labels: ['1er Trimestre', '2do  Trimestre', '13er Trimestre', '4to Trimestre'], datasets: [{ label: 'Casos', data: [50, 60, 70, 180] }, { label: 'Muertes', data: [100, 200, 300, 400] }] }}"
          ></img>
          <div className="card-body">
            <h5 className="card-title">{country}</h5>
            <br></br>
            <br></br>
            <p className="card-text">
              Cantidad de casos registrados {confirmed}
            </p>
            <p className="card-text">Cantidad de muertes {deaths}</p>
            <p className="card-text">Cantidad de recuperados {recovered}</p>
            <p className="card-text">Actualizado al {updated}</p>
            <br></br>
            <br></br>
          </div>
        </div>
      </div>
      <br></br>
    </Fragment>
  );
}

export default Pais;
